import { useState } from 'react';

interface ProductCardProps {
  id: number;
  name: string;
  price: number;
  image: string;
  category: string;
}

const ProductCard = ({ name, price, image, category }: ProductCardProps) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="relative group"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Pop Frame Container */}
      <div className={`pop-frame bg-card overflow-hidden transition-all duration-300 ${isHovered ? 'scale-105' : ''}`}>
        {/* Image with Halftone Effect */}
        <div className="relative overflow-hidden aspect-square">
          <img
            src={image}
            alt={name}
            className="w-full h-full object-cover transition-transform duration-500"
            style={{ transform: isHovered ? 'scale(1.1)' : 'scale(1)' }}
          />
          {/* Halftone Overlay */}
          <div className="absolute inset-0 halftone-bg pointer-events-none opacity-30"></div>

          {/* Category Badge */}
          <div className="absolute top-4 left-4 bg-accent text-accent-foreground px-4 py-2 font-bold border-4 border-foreground rotate-[-5deg] handwritten">
            {category}
          </div>

          {/* Comic-style "BANG" badge */}
          <div className="absolute top-4 right-4 bg-secondary text-secondary-foreground px-3 py-1 border-3 border-foreground rotate-[8deg] text-xs font-bold">
            NEW!
          </div>
        </div>

        {/* Product Info */}
        <div className="p-6 bg-card relative">
          {/* Dots decoration */}
          <div className="absolute top-0 left-0 w-20 h-20 dots-pattern text-primary opacity-20 pointer-events-none"></div>

          <h3 className="text-2xl font-black mb-2 text-foreground handwritten relative z-10">
            {name}
          </h3>

          <div className="flex items-center justify-between relative z-10">
            <span className="text-3xl font-black text-primary handwritten">
              ${price}
            </span>

            {/* Star decoration */}
            <div className="text-accent text-4xl animate-wiggle">★</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;

import { ButtonHTMLAttributes, useState } from 'react';

interface PopButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'accent';
  children: React.ReactNode;
}

const PopButton = ({ variant = 'primary', children, className = '', ...props }: PopButtonProps) => {
  const [isClicked, setIsClicked] = useState(false);

  const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    setIsClicked(true);
    setTimeout(() => setIsClicked(false), 600);
    props.onClick?.(e);
  };

  const variantStyles = {
    primary: 'bg-primary text-primary-foreground hover:bg-primary/90',
    secondary: 'bg-secondary text-secondary-foreground hover:bg-secondary/90',
    accent: 'bg-accent text-accent-foreground hover:bg-accent/90',
  };

  return (
    <button
      {...props}
      onClick={handleClick}
      className={`
        relative px-8 py-4 font-black text-lg handwritten
        border-4 border-foreground
        transition-all duration-200
        shadow-[4px_4px_0_hsl(var(--foreground))]
        hover:shadow-[6px_6px_0_hsl(var(--foreground))]
        hover:translate-x-[-2px] hover:translate-y-[-2px]
        active:shadow-[2px_2px_0_hsl(var(--foreground))]
        active:translate-x-[2px] active:translate-y-[2px]
        ${variantStyles[variant]}
        ${isClicked ? 'animate-bounce-pop' : ''}
        ${className}
      `}
    >
      {children}

      {/* Comic-style sparkles */}
      <span className="absolute -top-2 -right-2 text-accent text-2xl animate-pulse">
        ✦
      </span>
    </button>
  );
};

export default PopButton;

