          ) : (
            <div className="space-y-4">
              {addresses.map((address) => (
                <div
                  key={address.id}
                  className={`border-4 border-foreground p-6 relative transition-all ${
                    selectedAddressId === address.id
                      ? 'bg-accent/20 ring-4 ring-accent'
